INSERT INTO "exercises" VALUES ('Inverted Row','Traps (mid-back)','invertedrow',10,'Lay underneath a fixed horizontal bar and grab the bar with a wide overhand grip. Pull your body straight up to the bar. Return to the starting position, your arms fully extended. Repeat.');
