INSERT INTO "exercises" VALUES ('Donkey Kick','Hamstrings','donkeykick',10,'Get down on all fours and position your hands under your shoulders and your knees under your hips. Kick back with one leg and squeeze your glutes. Slowly return to starting position by bending your knee and lowering your leg. Switch Legs. Repeat.');
